import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link as RouterLink, Navigate } from 'react-router-dom';
import { ThemeProvider, createTheme, CssBaseline, AppBar, Toolbar, Typography, Container, Button, Box } from '@mui/material';
import Login from '../pages/Login';
import Feed from '../pages/Feed';
import Chat from '../pages/Chat';

const theme = createTheme({ palette: { mode: 'light' } });

function RequireAuth({ children }) {
  const token = localStorage.getItem('token');
  if (!token) return <Navigate to="/" replace />;
  return children;
}

function App() {
  const token = localStorage.getItem('token');
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              College Help
            </Typography>
            <Button color="inherit" component={RouterLink} to="/">Login</Button>
            <Button color="inherit" component={RouterLink} to="/feed">Feed</Button>
            <Button color="inherit" component={RouterLink} to="/chat">Chat</Button>
            {token && <Button color="inherit" onClick={() => { localStorage.removeItem('token'); window.location.href = '/'; }}>Logout</Button>}
          </Toolbar>
        </AppBar>
        <Box sx={{ mt: 4 }}>
          <Container maxWidth="md">
            <Routes>
              <Route path="/" element={<Login />} />
              <Route path="/feed" element={
                <RequireAuth>
                  <Feed />
                </RequireAuth>
              } />
              <Route path="/chat" element={
                <RequireAuth>
                  <Chat />
                </RequireAuth>
              } />
            </Routes>
          </Container>
        </Box>
      </Router>
    </ThemeProvider>
  );
}

export default App;
